function hightlightPix(x, y, minC1, maxC1, minC2, maxC2, add)
    local c = pix(x, y)
    local r1 = (c > minC1 and c <= maxC1)
    local r2 = (c > minC2 and c <= maxC2)

    if not (r1 or r2) then
        return
    end

    c = c + add
    local max = r1 and maxC1 or maxC2

    pix(x, y, c > max and max or c)
end

