            end
        end
    end

    xm = xm * 1.5
    ym = ym * 1.5

    for x = -24, 24 do
        local xx = xm + x * 30
        if xx >= 0 and xx < CORE_WIDTH then
            for y = 0, CORE_HEIGHT_1 do
                if pix(xx, y) ~= 0 then
