    spr(s.idx,
        s.offX + (offX + 50 + 40 * sin(.3 + .5 * t.elapsed)) // 1,
        55,
        s.bgCol, 1, 0, s.rot, s.sprW, s.sprH)

    s = _runnerSprites[(idx + 2) % #_runnerSprites + 1]
    spr(s.idx,
        s.offX + (offX + 80 + 70 * sin(.6 + .4 * t.elapsed)) // 1,
