        max = #_voxLinesSpectrox * _voxLinesSpectroxFactor[1]
        for i = 1, max do
            yy = 7 + _voxLinesSpectrox[i]
            -- Color 1 is used for BDR.
            line(0, yy, 240, yy, 1)
